import 'package:flutter/material.dart';
import 'save_seed_screen.dart';
import 'recover_seed_screen.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Seed Phrase Security',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      initialRoute: '/',
      routes: {
        '/': (context) => HomeScreen(),
        '/save': (context) => SaveSeedScreen(),
        '/recover': (context) => RecoverSeedScreen(),
      },
    );
  }
}

class HomeScreen extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('Home')),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            ElevatedButton(
              onPressed: () {
                Navigator.pushNamed(context, '/save');
              },
              child: Text('Save Seed Phrase'),
            ),
            ElevatedButton(
              onPressed: () {
                Navigator.pushNamed(context, '/recover');
              },
              child: Text('Recover Seed Phrase'),
            ),
          ],
        ),
      ),
    );
  }
}
